import React, { Component } from 'react';
import {Row,Col,Card} from 'react-bootstrap';
import axios from 'axios';
export default class WorldCard extends Component {
    state={
        result:[],
    }
    componentDidMount(){
        axios.get('https://covid19.mathdro.id/api').then(res=> this.setState({result:res.data})).catch(err=>console.log('error to get worlddata'));
    }
    render() {
        return (
        <div>
                <Row >
                    <Col sm>
                    <Card  style={{ width: '18rem' }}>
                    
                        <Card.Body>
                            <Card.Title>{this.state.result}</Card.Title>
                            <Card.Text>
                            Confirmed
                            </Card.Text>
                            
                        </Card.Body>
                    </Card>
                    </Col>
                    <Col sm>
                    <Card  style={{ width: '18rem' }}>
                    
                        <Card.Body>
                            <Card.Title>Card Title</Card.Title>
                            <Card.Text>
                            Recovered
                            </Card.Text>
                            
                        </Card.Body>
                    </Card>
                    </Col>
                    <Col sm>
                    <Card  style={{ width: '18rem' }}>
                    
                        <Card.Body>
                            <Card.Title>Card Title</Card.Title>
                            <Card.Text>
                            Deaths
                            </Card.Text>
                            
                        </Card.Body>
                    </Card>
                    </Col>
                </Row>
        </div>
        );
    }
    }
